                  <div className="add-contact-box">
                    <div className="add-contact-content">
                      <div className="image-placeholder">
                        <div className="avatar-edit">
                          <input
                            type="file"
                            onChange={fileHandler}
                            id="imageUpload"
                            onClick={(event) => setFile(event.target.value)}
                          />
                          <label htmlFor="imageUpload" name=""></label>
                        </div>
                        <div className="avatar-preview">
                          <div id="imagePreview">
                            <img
                              src={file ? URL.createObjectURL(file) : user}
                              alt={file ? file.name : null}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="form-group">
                        <label className="text-black font-w500">
                          Nom et Prenom
                        </label>
                        <div className="contact-name">
                          <input
                            type="text"
                            id="c-name"
                            className="form-control"
                            autocomplete="off"
                            value={list_name}
                            onChange={(event) =>
                              setInputData(event.target.value)
                            }
                            placeholder="Name"
                          />
                          <span className="validation-text"></span>
                        </div>
                      </div>
                      <div className="form-group">
                        <label className="text-black font-w500">Address</label>
                        <div className="contact-occupation">
                          <input
                            type="text"
                            id="c-occupation"
                            autocomplete="off"
                            value={list_occupation}
                            onChange={(event) =>
                              setInputData1(event.target.value)
                            }
                            className="form-control"
                            placeholder="Adress"
                          />
                        </div>
                      </div>

					  <div style={{ border: "1px dashed red", padding: "15px" }}>
                      <div style={{ display: "flex" }}>
                        <div className="form-group col-md-6">
                          <label className="text-black font-w500">
                            Id card
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="J552203"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-6">
                          <label className="text-black font-w500">
                            Valable Jusqu'au
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2027"
                            />
                          </div>
                        </div>
                      </div>

					  <div style={{ marginLeft: "40%", marginBottom: "10px"}}>OU BIEN</div>

                      <div style={{ display: "flex" }}>
                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
                            Passport
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="J552203"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
						  	Delivre Le 
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2027"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
                            A
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2027"
                            />
                          </div>
                        </div>
                      </div>

					  </div>


                      <div style={{ display: "flex" }}>
                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
                            Permit
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="J552203"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
						  	Delivre Le 
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2019"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
                            A
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={list_occupation}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2027"
                            />
                          </div>
                        </div>
                      </div>

                     
                    </div>
                  </div>
                </div>
                <div className="modal-footer">
                  {/*<button type="submit" id="btn-edit" className="float-left btn btn-primary">Save</button>*/}
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => setAddContact(false)}
                  >
                    {" "}
                    <i className="flaticon-delete-1"></i> Annuler
                  </button>
                  <button id="btn-add" className="btn btn-primary">
                    Ajouter
                  </button>
                </div>
              </form>
            </div>
          </div>
        </Modal>
      </div>
      <div className="tab-content">
        <div className="tab-pane fade show active" id="navpills-1">
          <TabContent activeTab={activeTab}>
            <TabPane tabId="1">
              <PerfectScrollbar
                className="row dz-scroll  loadmore-content searchable-items list"
                id="allContactListContent"
              >
                <div className="items items-header-section"></div>

                {data.map((item) => (
                  <div
                    className="col-xl-3 col-xxl-4 col-lg-4 col-md-6 col-sm-6 items"
                    key={item.id}
                  >
                    <div className="card contact-bx item-content">
                      <div className="card-header border-0">
                        <div className="action-dropdown">
                          <Dropdown className="">
                            <Dropdown.Toggle
                              variant=""
                              as="div"
                              className="i-false"
                            >
                              <Link
                                to={"#"}
                                data-toggle="dropdown"
                                aria-expanded="false"
                              >
                                <svg
                                  width="24"
                                  height="24"
                                  viewBox="0 0 24 24"
                                  fill="none"
                                  xmlns="http://www.w3.org/2000/svg"
                                >
                                  <path
                                    d="M12 13C12.5523 13 13 12.5523 13 12C13 11.4477 12.5523 11 12 11C11.4477 11 11 11.4477 11 12C11 12.5523 11.4477 13 12 13Z"
                                    stroke="#575757"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                  ></path>
                                  <path
                                    d="M12 6C12.5523 6 13 5.55228 13 5C13 4.44772 12.5523 4 12 4C11.4477 4 11 4.44772 11 5C11 5.55228 11.4477 6 12 6Z"
                                    stroke="#575757"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                  ></path>
                                  <path
                                    d="M12 20C12.5523 20 13 19.5523 13 19C13 18.4477 12.5523 18 12 18C11.4477 18 11 18.4477 11 19C11 19.5523 11.4477 20 12 20Z"
                                    stroke="#575757"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                  ></path>
                                </svg>
                              </Link>
                            </Dropdown.Toggle>
                            <Dropdown.Menu
                              className="dropdown-menu-right"
                              alignRight={true}
                            >
                              <Dropdown.Item
                                className="delete text-danger"
                                onClick={() => handleRemove(item.id)}
                              >
                                Delete
                              </Dropdown.Item>
                            </Dropdown.Menu>
                          </Dropdown>
                        </div>
                      </div>
                      <div className="card-body user-profile">
                        <div className="image-bx">
                          <img
                            src={item.image}
                            alt=""
                            className="rounded-circle"
                          />
                          <span className="active"></span>
                        </div>
                        <div className="media-body user-meta-info">
                          <h6 className="fs-20 font-w500 my-1">
                            <Link
                              to={"./app-profile"}
                              className="text-black user-name"
                              data-name="Engeline O’conner"
                            >
                              {item.username}
                            </Link>
                          </h6>
                          <p
                            className="fs-14 mb-3 user-work"
                            data-occupation="UI Designer"
                          >
                            {item.occupation}
                          </p>
                          <ul>
                            <li>
                              <Link to={"#"}>
                                <i
                                  className="fa fa-phone"
                                  aria-hidden="true"
                                ></i>
                              </Link>
                            </li>
                            <li>
                              <Link to={"./messages"}>
                                <i className="las la-sms"></i>
                              </Link>
                            </li>
                            <li>
                              <Link to={"#"}>
                                <i
                                  className="fa fa-video-camera"
                                  aria-hidden="true"
                                ></i>
                              </Link>
                            </li>
                          </ul>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}

                {list.map((elem) => {
                  return (
                    <div
                      className="col-xl-3 col-xxl-4 col-lg-4 col-md-6 col-sm-6 items"
                      key={elem.id}
                    >
                      <div className="card contact-bx item-content">
                        <div className="card-header border-0">
                          <div className="action-dropdown">
                            <Dropdown className="">
                              <Dropdown.Toggle
                                variant=""
                                as="div"
                                className="i-false"
                              >
                                <Link
                                  to={"#"}
                                  data-toggle="dropdown"
                                  aria-expanded="false"
                                >
                                  <svg
                                    width="24"
                                    height="24"
                                    viewBox="0 0 24 24"
                                    fill="none"
                                    xmlns="http://www.w3.org/2000/svg"
                                  >
                                    <path
                                      d="M12 13C12.5523 13 13 12.5523 13 12C13 11.4477 12.5523 11 12 11C11.4477 11 11 11.4477 11 12C11 12.5523 11.4477 13 12 13Z"
                                      stroke="#575757"
                                      strokeWidth="2"
                                      strokeLinecap="round"
                                      strokeLinejoin="round"
                                    ></path>
                                    <path
                                      d="M12 6C12.5523 6 13 5.55228 13 5C13 4.44772 12.5523 4 12 4C11.4477 4 11 4.44772 11 5C11 5.55228 11.4477 6 12 6Z"
                                      stroke="#575757"
                                      strokeWidth="2"
                                      strokeLinecap="round"
                                      strokeLinejoin="round"
                                    ></path>
                                    <path
                                      d="M12 20C12.5523 20 13 19.5523 13 19C13 18.4477 12.5523 18 12 18C11.4477 18 11 18.4477 11 19C11 19.5523 11.4477 20 12 20Z"
                                      stroke="#575757"
                                      strokeWidth="2"
                                      strokeLinecap="round"
                                      strokeLinejoin="round"
                                    ></path>
                                  </svg>
                                </Link>
                              </Dropdown.Toggle>
                              <Dropdown.Menu
                                className="dropdown-menu-right"
                                alignRight={true}
                              >
                                <Dropdown.Item
                                  className="delete text-danger"
                                  onClick={() => dispatch(deleteTodo(elem.id))}
                                >
                                  Delete
                                </Dropdown.Item>
                              </Dropdown.Menu>
                            </Dropdown>
                          </div>
                        </div>
                        <div className="card-body user-profile">
                          <div className="image-bx">
                            <img
                              src={
                                elem.data.file
                                  ? URL.createObjectURL(elem.data.file)
                                  : user
                              }
                              alt=""
                              className="rounded-circle"
                            />
                            <span className="active"></span>
                          </div>
                          <div className="media-body user-meta-info">
                            <h6 className="fs-20 font-w500 my-1">
                              <Link
                                to={"./app-profile"}
                                className="text-black user-name"
                                data-name="Alan Green"
                              >
                                {elem.data.list_name}
                              </Link>
                            </h6>
                            <p
                              className="fs-14 mb-3 user-work"
                              data-occupation="UI Designer"
                            >
                              {elem.data.list_occupation}
                            </p>
                            <ul>
                              <li>
                                <Link to={"#"}>
                                  <i
                                    className="fa fa-phone"
                                    aria-hidden="true"
                                  ></i>
                                </Link>
                              </li>
                              <li>
                                <Link to={"./messages"}>
                                  <i className="las la-sms"></i>
                                </Link>
                              </li>
                              <li>
                                <Link to={"#"}>
                                  <i
                                    className="fa fa-video-camera"
                                    aria-hidden="true"
                                  ></i>
                                </Link>
                              </li>
                            </ul>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </PerfectScrollbar>
              <div className="row">
                <div className="col-xl-12 d-flex justify-content-center">
                  <Link
                    to={"#"}
                    className="btn btn-outline-primary dz-load-more"
                    onClick={() => onClick()}
                  >
                    Load More<span>&#187;</span>{" "}
                    {refresh && <i className="fa fa-refresh"></i>}
                  </Link>
                </div>
              </div>
            </TabPane>
            <TabPane tabId="2">
              <ContactsData />
            </TabPane>
          </TabContent>
        </div>
      </div>
    </Fragment>
  );
};
export default Contacts;
