import React, { Fragment, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Dropdown, Modal } from "react-bootstrap";
import { TabContent, TabPane } from "reactstrap";
import swal from "sweetalert";
import PerfectScrollbar from "react-perfect-scrollbar";

import { addTodo, deleteTodo } from "../../../store/actions/index";
import { useSelector, useDispatch } from "react-redux";


//Images
import user from "./../../../images/contacts/user.jpg";
import contact7 from "./../../../images/contacts/Untitled-7.jpg";
import contact6 from "./../../../images/contacts/Untitled-6.jpg";
import contact5 from "./../../../images/contacts/Untitled-5.jpg";
import contact4 from "./../../../images/contacts/Untitled-4.jpg";
import contact3 from "./../../../images/contacts/Untitled-3.jpg";
import contact2 from "./../../../images/contacts/Untitled-2.jpg";
import contact1 from "./../../../images/contacts/Untitled-1.jpg";
import axios from "axios";



const Contacts = () => {

  const [clients, setClients] = useState([]);
  const [nbClients, setNbClients] = useState([]);
  const PF = "http://localhost:8800/images/"

  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get("http://localhost:8800/api/clients");
      setClients(res.data)
    };
    fetchUser();
  }, []);


  useEffect(() => {
    const fetchResult = async () => {
      const res = await axios.get("http://localhost:8800/api/clients/result");

      setNbClients(res.data);
    };
    fetchResult();
  }, []);


  //tab function
  const [activeTab, setActiveTab] = useState("1");


  // This is Model function
  const [addContact, setAddContact] = useState(false);

  //Remove and delete List from Blog 'function'

  function handleRemove(_id) {
    const newList = clients.filter((item) => item._id !== _id);
    setClients(newList);
  }


  //Add and delete data from list
  const dispatch = useDispatch();
  let list = useSelector((state) => state.todoReducers.list);
  const [fullName, setFullName] = useState("");
  const [lieu, setLieu] = useState("");
  const [date, setDate] = useState("");
  const [idCard, setIdCard] = useState("");
  const [valable, setValable] = useState("");
  const [passport, setPassport] = useState("");
  const [pLe, setPLe] = useState("");
  const [pA, setPa] = useState("");
  const [permit, setPermit] = useState("");
  const [perLe, setPerLe] = useState("");
  const [perA, setPerA] = useState("");
  const [tel, setTel] = useState("");
  const [email, setEmail] = useState("");

  const dataArr = [];

  //console.log(list);

  //Error message
  function onAddData(e) {
    e.preventDefault();
    var error = false;
    var errorMsg = "";
    if (fullName === "") {
      error = true;
      errorMsg = "Please fill name.";
    } else if (lieu === "") {
      error = true;
      errorMsg = "Please fill occupation.";
    }

    if (!error) {
      //fetch data from Form
      dataArr.fullName = fullName;
      dataArr.lieu = lieu;
      dataArr.file = file;

      dispatch(addTodo(dataArr));
 
      setAddContact(false);
      swal("Terminé !", "Ajouté avec succès", "success");
    } else {
      swal("Oops", errorMsg, "error");
    }
  }

  //For Image upload in ListBlog
  const [file, setFile] = React.useState(null);
  console.log(file);
  const fileHandler = (e) => {
    setFile(e.target.files[0]);
  };

  return (
    <Fragment>
      <div className="project-nav">
        <div className="card-action card-tabs  mr-auto">
          <ul className="nav nav-tabs style-2">
            <li className="nav-item">
              <Link to={"#"} className={"active nav-link"}>
                Tous Les Clients
                <span className="ml-1 badge badge-primary shadow-primary">
                  {nbClients}
                </span>
              </Link>
            </li>
          </ul>
        </div>
        <div className="d-flex align-items-center">
          <Link
            to={"#"}
            id="btn-add-contact"
            onClick={() => setAddContact(true)}
            className="btn btn-primary rounded text-white"
          >
            + Nouveau Client
          </Link>
        </div>
        {/* <!-- Modal --> */}
        <Modal
          className="modal fade"
          id="addContactModal"
          show={addContact}
          onHide={setAddContact}
        >
          <div className="" role="document">
            <div className="">
              <form id="addContactModalTitle" onSubmit={onAddData}>
                <div className="modal-header">
                  <h4 className="modal-title fs-20">Ajouter Client</h4>
                  <button
                    type="button"
                    className="close"
                    onClick={() => setAddContact(false)}
                    data-dismiss="modal"
                  >
                    <span>&times;</span>
                  </button>
                </div>
                <div className="modal-body">
                  <i
                    className="flaticon-cancel-12 close"
                    data-dismiss="modal"
                  ></i>
                  <div className="add-contact-box">
                    <div className="add-contact-content">
                      <div className="image-placeholder">
                        <div className="avatar-edit">
                          <input
                            type="file"
                            onChange={fileHandler}
                            id="imageUpload"
                            onClick={(event) => setFile(event.target.value)}
                          />
                          <label htmlFor="imageUpload" name=""></label>
                        </div>
                        <div className="avatar-preview">
                          <div id="imagePreview">
                            <img
                              src={file ? URL.createObjectURL(file) : user}
                              alt={file ? file.name : null}
                            />
                          </div>
                        </div>
                      </div>
                      <div className="form-group">
                        <label className="text-black font-w500">
                          Nom et Prenom
                        </label>
                        <div className="contact-name">
                          <input
                            type="text"
                            id="c-name"
                            className="form-control"
                            autocomplete="off"
                            value={fullName}
                            onChange={(event) =>
                              setFullName(event.target.value)
                            }
                            placeholder="Name"
                          />
                          <span className="validation-text"></span>
                        </div>
                      </div>
                      <div className="form-group">
                        <label className="text-black font-w500">
                          Lieu de Naissance
                        </label>
                        <div className="contact-occupation">
                          <input
                            type="text"
                            id="c-occupation"
                            autocomplete="off"
                            value={lieu}
                            onChange={(event) =>
                              setLieu(event.target.value)
                            }
                            className="form-control"
                            placeholder="Adress"
                          />
                        </div>
                      </div>

                      <div className="form-group">
                        <label className="text-black font-w500">
                          Date de Naissance
                        </label>
                        <div className="contact-occupation">
                          <input
                            type="text"
                            id="c-occupation"
                            autocomplete="off"
                            value={date}
                            onChange={(event) =>
                              setDate(event.target.value)
                            }
                            className="form-control"
                            placeholder="12-01-1992"
                          />
                        </div>
                      </div>

                      <div
                        style={{
                          border: "2px solid #e8e8e8",
                          borderRadius: "25px",
                          padding: "15px",
                          marginBottom: "20px",
                        }}
                      >
                        <div style={{ display: "flex" }}>
                          <div className="form-group col-md-6">
                            <label className="text-black font-w500">
                              Nº Id card
                            </label>
                            <div className="contact-occupation">
                              <input
                                type="text"
                                id="c-occupation"
                                autocomplete="off"
                                value={idCard}
                                onChange={(event) =>
                                  setIdCard(event.target.value)
                                }
                                className="form-control "
                                placeholder="J552203"
                              />
                            </div>
                          </div>

                          <div className="form-group col-md-6">
                            <label className="text-black font-w500">
                              Valable Jusqu'au
                            </label>
                            <div className="contact-occupation">
                              <input
                                type="text"
                                id="c-occupation"
                                autocomplete="off"
                                value={valable}
                                onChange={(event) =>
                                  setValable(event.target.value)
                                }
                                className="form-control "
                                placeholder="10-12-2027"
                              />
                            </div>
                          </div>
                        </div>

                        <div
                          style={{ marginLeft: "40%", marginBottom: "10px" }}
                        >
                          OU BIEN
                        </div>

                        <div style={{ display: "flex" }}>
                          <div className="form-group col-md-4">
                            <label className="text-black font-w500">
                              Nº Passport
                            </label>
                            <div className="contact-occupation">
                              <input
                                type="text"
                                id="c-occupation"
                                autocomplete="off"
                                value={lieu}
                                onChange={(event) =>
                                  setInputData1(event.target.value)
                                }
                                className="form-control "
                                placeholder="J552203"
                              />
                            </div>
                          </div>

                          <div className="form-group col-md-4">
                            <label className="text-black font-w500">
                              Delivre Le
                            </label>
                            <div className="contact-occupation">
                              <input
                                type="text"
                                id="c-occupation"
                                autocomplete="off"
                                value={lieu}
                                onChange={(event) =>
                                  setInputData1(event.target.value)
                                }
                                className="form-control "
                                placeholder="10-12-2027"
                              />
                            </div>
                          </div>

                          <div className="form-group col-md-4">
                            <label className="text-black font-w500">A</label>
                            <div className="contact-occupation">
                              <input
                                type="text"
                                id="c-occupation"
                                autocomplete="off"
                                value={lieu}
                                onChange={(event) =>
                                  setInputData1(event.target.value)
                                }
                                className="form-control "
                                placeholder="10-12-2027"
                              />
                            </div>
                          </div>
                        </div>
                      </div>

                      <div style={{ display: "flex" }}>
                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
                            Nº Permit
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={lieu}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="J552203"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">
                            Delivre Le
                          </label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={lieu}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2019"
                            />
                          </div>
                        </div>

                        <div className="form-group col-md-4">
                          <label className="text-black font-w500">A</label>
                          <div className="contact-occupation">
                            <input
                              type="text"
                              id="c-occupation"
                              autocomplete="off"
                              value={lieu}
                              onChange={(event) =>
                                setInputData1(event.target.value)
                              }
                              className="form-control "
                              placeholder="10-12-2027"
                            />
                          </div>
                        </div>
                      </div>

					  <div className="form-group">
                        <label className="text-black font-w500">
                          Tel
                        </label>
                        <div className="contact-occupation">
                          <input
                            type="text"
                            id="c-occupation"
                            autocomplete="off"
                            value={lieu}
                            onChange={(event) =>
                              setInputData1(event.target.value)
                            }
                            className="form-control"
                            placeholder="0678987641"
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="modal-footer">
                  {/*<button type="submit" id="btn-edit" className="float-left btn btn-primary">Save</button>*/}
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => setAddContact(false)}
                  >
                    {" "}
                    <i className="flaticon-delete-1"></i> Annuler
                  </button>
                  <button id="btn-add" className="btn btn-primary" >
                    Ajouter
                  </button>
                </div>
              </form>
            </div>
          </div>
        </Modal>
      </div>
      <div className="tab-content">
        <div className="tab-pane fade show active" id="navpills-1">
          <TabContent activeTab={activeTab}>
            <TabPane tabId="1">
              <PerfectScrollbar
                className="row dz-scroll  loadmore-content searchable-items list"
                id="allContactListContent"
              >
                <div className="items items-header-section"></div>

                {clients.map((item) => (
                  <div
                    className="col-xl-3 col-xxl-4 col-lg-4 col-md-6 col-sm-6 items"
                    key={item._id}
                  >
                    <div className="card contact-bx item-content">
                      <div className="card-header border-0">
                        <div className="action-dropdown">
                          <Dropdown className="">
                            <Dropdown.Toggle
                              variant=""
                              as="div"
                              className="i-false"
                            >
                              <Link
                                to={"#"}
                                data-toggle="dropdown"
                                aria-expanded="false"
                              >
                                <svg
                                  width="24"
                                  height="24"
                                  viewBox="0 0 24 24"
                                  fill="none"
                                  xmlns="http://www.w3.org/2000/svg"
                                >
                                  <path
                                    d="M12 13C12.5523 13 13 12.5523 13 12C13 11.4477 12.5523 11 12 11C11.4477 11 11 11.4477 11 12C11 12.5523 11.4477 13 12 13Z"
                                    stroke="#575757"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                  ></path>
                                  <path
                                    d="M12 6C12.5523 6 13 5.55228 13 5C13 4.44772 12.5523 4 12 4C11.4477 4 11 4.44772 11 5C11 5.55228 11.4477 6 12 6Z"
                                    stroke="#575757"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                  ></path>
                                  <path
                                    d="M12 20C12.5523 20 13 19.5523 13 19C13 18.4477 12.5523 18 12 18C11.4477 18 11 18.4477 11 19C11 19.5523 11.4477 20 12 20Z"
                                    stroke="#575757"
                                    strokeWidth="2"
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                  ></path>
                                </svg>
                              </Link>
                            </Dropdown.Toggle>
                            <Dropdown.Menu
                              className="dropdown-menu-right"
                              alignRight={true}
                            >
                              <Dropdown.Item
                                className="delete text-danger"
                                onClick={() => handleRemove(item._id)}
                              >
                                Delete
                              </Dropdown.Item>
                            </Dropdown.Menu>
                          </Dropdown>
                        </div>
                      </div>
                      <div className="card-body user-profile">
                        <div className="image-bx">
                          <img
                            src={ item.profilePicture
                              ? PF + item.profilePicture
                              : PF + "noAvatar.png"}
                            alt=""
                            className="rounded-circle"
                          />
                        </div>
                        <div className="media-body user-meta-info">
                          <h6 className="fs-20 font-w500 my-1">
                            <Link
                              to={"./app-profile"}
                              className="text-black user-name"
                              data-name="Engeline O’conner"
                            >
                              {item.fullName}
                            </Link>
                          </h6>
                          <p
                            className="fs-14 mb-3 user-work"
                            data-occupation="UI Designer"
                          >
                            {item.id}
                          </p>
                          <ul>
                            <li>
                              <Link to={"#"}>
                                <i
                                  className="fa fa-phone"
                                  aria-hidden="true"
                                ></i>
                              </Link>
                            </li>
                            <li>
                              <Link to={"#"}>
                                <i
                                  className="fa  fa-address-book								  "
                                  aria-hidden="true"
                                ></i>
                              </Link>
                            </li>
                          </ul>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}

                {list.map((elem) => {
                  return (
                    <div
                      className="col-xl-3 col-xxl-4 col-lg-4 col-md-6 col-sm-6 items"
                      key={elem.id}
                    >
                      <div className="card contact-bx item-content">
                        <div className="card-header border-0">
                          <div className="action-dropdown">
                            <Dropdown className="">
                              <Dropdown.Toggle
                                variant=""
                                as="div"
                                className="i-false"
                              >
                                <Link
                                  to={"#"}
                                  data-toggle="dropdown"
                                  aria-expanded="false"
                                >
                                  <svg
                                    width="24"
                                    height="24"
                                    viewBox="0 0 24 24"
                                    fill="none"
                                    xmlns="http://www.w3.org/2000/svg"
                                  >
                                    <path
                                      d="M12 13C12.5523 13 13 12.5523 13 12C13 11.4477 12.5523 11 12 11C11.4477 11 11 11.4477 11 12C11 12.5523 11.4477 13 12 13Z"
                                      stroke="#575757"
                                      strokeWidth="2"
                                      strokeLinecap="round"
                                      strokeLinejoin="round"
                                    ></path>
                                    <path
                                      d="M12 6C12.5523 6 13 5.55228 13 5C13 4.44772 12.5523 4 12 4C11.4477 4 11 4.44772 11 5C11 5.55228 11.4477 6 12 6Z"
                                      stroke="#575757"
                                      strokeWidth="2"
                                      strokeLinecap="round"
                                      strokeLinejoin="round"
                                    ></path>
                                    <path
                                      d="M12 20C12.5523 20 13 19.5523 13 19C13 18.4477 12.5523 18 12 18C11.4477 18 11 18.4477 11 19C11 19.5523 11.4477 20 12 20Z"
                                      stroke="#575757"
                                      strokeWidth="2"
                                      strokeLinecap="round"
                                      strokeLinejoin="round"
                                    ></path>
                                  </svg>
                                </Link>
                              </Dropdown.Toggle>
                              <Dropdown.Menu
                                className="dropdown-menu-right"
                                alignRight={true}
                              >
                                <Dropdown.Item
                                  className="delete text-danger"
                                  onClick={() => dispatch(deleteTodo(elem.id))}
                                >
                                  Delete
                                </Dropdown.Item>
                              </Dropdown.Menu>
                            </Dropdown>
                          </div>
                        </div>
                        <div className="card-body user-profile">
                          <div className="image-bx">
                            <img
                              src={
                                elem.data.file
                                  ? URL.createObjectURL(elem.data.file)
                                  : user
                              }
                              alt=""
                              className="rounded-circle"
                            />
                            <span className="active"></span>
                          </div>
                          <div className="media-body user-meta-info">
                            <h6 className="fs-20 font-w500 my-1">
                              <Link
                                to={"./app-profile"}
                                className="text-black user-name"
                                data-name="Alan Green"
                              >
                                {elem.data.fullName}
                              </Link>
                            </h6>
                            <p
                              className="fs-14 mb-3 user-work"
                              data-occupation="UI Designer"
                            >
                              {elem.data.lieu}
                            </p>
                            <ul>
                            <li>
                              <Link to={"#"}>
                                <i
                                  className="fa fa-phone"
                                  aria-hidden="true"
                                ></i>
                              </Link>
                            </li>
                            <li>
                              <Link to={"#"}>
                                <i
                                  className="fa  fa-address-book								  "
                                  aria-hidden="true"
                                ></i>
                              </Link>
                            </li>
                          </ul>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </PerfectScrollbar>
            </TabPane>
          </TabContent>
        </div>
      </div>
    </Fragment>
  );
};
export default Contacts;
