/// Menu
import MetisMenu from "metismenujs";
import React, { Component, useState, useContext, useEffect } from "react";
import {Modal} from 'react-bootstrap';
/// Scroll
import PerfectScrollbar from "react-perfect-scrollbar";
/// Link
import { Link } from "react-router-dom";
import useScrollPosition from "use-scroll-position";
import { ThemeContext } from "../../../context/ThemeContext";
//import profile from "../../../images/Untitled-1.jpg";
class MM extends Component {
  componentDidMount() {
    this.$el = this.el;
    this.mm = new MetisMenu(this.$el);
  }
  componentWillUnmount() {
    //  this.mm("dispose");
    // console.log(this.mm);
  }
  render() {
    return (
      <div className="mm-wrapper">
        <ul className="metismenu" ref={(el) => (this.el = el)}>
          {this.props.children}
        </ul>
      </div>
    );
  }
}

const SideBar = () => {
	const [newProject ,setNewProject] = useState(false);
  const {
    iconHover,
    sidebarposition,
    headerposition,
    sidebarLayout,
  } = useContext(ThemeContext);
	
  useEffect(() => {
    var btn = document.querySelector(".nav-control");
    var aaa = document.querySelector("#main-wrapper");
    function toggleFunc() {
      return aaa.classList.toggle("menu-toggle");
    }
    btn.addEventListener("click", toggleFunc);
	
	//sidebar icon Heart blast
	var handleheartBlast = document.querySelector('.heart');
        function heartBlast() {
            return handleheartBlast.classList.toggle("heart-blast");
        }

        handleheartBlast.addEventListener('click', heartBlast);
	
  }, []);
  let scrollPosition = useScrollPosition();
  /// Path
  let path = window.location.pathname;
  path = path.split("/");
  path = path[path.length - 1];
  /// Active menu
  let deshBoard = [
      ""
    ],
    app = [
      "app-profile",
    ],
    charts = [
      "projects",
    ],
    bootstrap = [
      "contacts",
    ],
    pdf =[
      "pdf-export",
    ];
  return (
    <div
      className={`deznav ${iconHover} ${
        sidebarposition.value === "fixed" &&
        sidebarLayout.value === "horizontal" &&
        headerposition.value === "static"
          ? scrollPosition > 120
            ? "fixed"
            : ""
          : ""
      }`}
    >
		{/* <!-- Add Project --> */}
		<Modal className="modal fade" id="addProjectSidebar" show={newProject} onHide={setNewProject}>
			<div className="" role="document">
				<div className="modal-content">
					<div className="modal-header">
						<h5 className="modal-title">Create Project</h5>
						<button type="button" className="close" onClick={() => setNewProject(false)}><span>&times;</span></button>
					</div>
					<div className="modal-body">
						<form>
							<div className="form-group">
								<label className="text-black font-w500">Project Name</label>
								<input type="text" className="form-control" />
							</div>
							<div className="form-group">
								<label className="text-black font-w500">Deadline</label>
								<input type="date" className="form-control" />
							</div>
							<div className="form-group">
								<label className="text-black font-w500">Client Name</label>
								<input type="text" className="form-control" />
							</div>
							<div className="form-group">
								<button type="button" className="btn btn-primary">CREATE</button>
							</div>
						</form>
					</div>
				</div>
			</div>
		</Modal>
      <PerfectScrollbar className="deznav-scroll">
	  {/* <div className="main-profile">
			<img src={profile} alt="" />
			<Link to={"#"}><i className="fa fa-cog" aria-hidden="true"></i></Link>
			<h5 className="mb-0 fs-20 text-black "><span className="font-w400">Hello,</span> Marquez</h5>
			<p className="mb-0 fs-14 font-w400">marquezzzz@mail.com</p>
	  </div> */}
		<Link to={"#"} className="add-project-sidebar btn btn-primary"   onClick={() => setNewProject(true)} > Nouvelle contrat</Link>
        <MM className="metismenu" id="menu">
          <li className={`${deshBoard.includes(path) ? "mm-active" : ""}`}>
				<Link className="ai-icon" to="/" > <i className="flaticon-047-home"></i><span className="nav-text">Dashboard</span></Link>
            
          </li>
          <li className={`${app.includes(path) ? "mm-active" : ""}`}>
            <Link className="has-arrow ai-icon" to="#" >
              <i className="flaticon-044-menu"></i>
              <span className="nav-text">Contrats</span>
            </Link>
            <ul >
            <li><Link className={`${ path === "table-datatable-basic" ? "mm-active" : "" }`} to="/table-bootstrap-basic">Tous Les Contrats</Link></li>
              <li><Link className={`${path === "form-wizard" ? "mm-active" : ""}`} to="/form-wizard">Nouveau Contrat</Link></li>
              <li><Link className={`${ path === "table-datatable-basic" ? "mm-active" : "" }`} to="/table-bootstrap-basic">Tous Les Avancements</Link></li>
            </ul>
          </li>

          <li className={`${bootstrap.includes(path) ? "mm-active" : ""}`}>
            <Link className="ai-icon" to="/clients"  >
              <i className="flaticon-086-star"></i>
              <span className="nav-text">Clients</span>
            </Link>

          </li>

          <li className={`${charts.includes(path) ? "mm-active" : ""}`}>
            <Link className="ai-icon" to="/voitures" >
              <i className="flaticon-038-gauge"></i>
              <span className="nav-text">Voitures</span>
            </Link>

          </li>

          <li className={`${pdf.includes(path) ? "mm-active" : ""}`}>
            <Link className="has-arrow ai-icon" to="/pdf-export" >
              <i className="flaticon-072-printer"></i>
              <span className="nav-text forms">Export PDF</span>
            </Link>
          </li>

          
        </MM>
		<div className="copyright">
			<p><strong>Location des voitures dashboard</strong> Â© 2021 All Rights Reserved</p>
			<p className="fs-12">Made with <span className="heart"></span> by Oussama&Houssam</p>
		</div>
      </PerfectScrollbar>
    </div>
  );
};

export default SideBar;
