import axios from "axios";
import React, { useEffect, useState } from "react";
import CustomClearIndicator from "../../PluginsMenu/Select2/MultiSelect2";

const StepOne = () => {

  const [voitures, setVoitures] = useState([]);


  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get("http://localhost:8800/api/clients");
      setVoitures(
        res.data.map((item) => {
          return { value: item._id, label: item.nom + "(" +item.matricule + ")" };
        })
      );
    };
    fetchUser();
  }, []);


   return (
      <section>
         <div className="row">
         <div className="col-xl-12">
          <div className="card">
            <div className="card-body " style={{textAlign: "left"}}>
              <div className="mb-4 " >
                <h4 className="card-title" >Deuxiement Choisir la Voiture </h4>
                <p>
                  Tu peut choisir un ou plusieur Voitures.
                </p>
              </div>
              <label className="select2-label">Chercher une voiture par son nom </label>
              <br />
              <div className="mt-4 pb-5">
                <CustomClearIndicator />
              </div>
            </div>
          </div>
        </div>
         </div>
      </section>
   );
};

export default StepOne;
