import React, { Fragment, useState } from "react";
import { Button, Dropdown, Modal } from "react-bootstrap";
import { Link } from "react-router-dom";
import { SRLWrapper } from "simple-react-lightbox";
//** Import Image */
import profile01 from "../../../../images/profile/1.jpg";
import profile02 from "../../../../images/profile/2.jpg";
import profile03 from "../../../../images/profile/3.jpg";
import profile04 from "../../../../images/profile/4.jpg";
import profile05 from "../../../../images/profile/5.jpg";
import profile06 from "../../../../images/profile/6.jpg";
import profile07 from "../../../../images/profile/7.jpg";
import profile08 from "../../../../images/profile/8.jpg";
import profile09 from "../../../../images/profile/9.jpg";
import profile from "../../../../images/profile/profile.png";
import PageTitle from "../../../layouts/PageTitle";

const AppProfile = () => {
	const [activeToggle, setActiveToggle] = useState("posts");
	const [sendMessage, setSendMessage] = useState(false);
	const [postModal, setPostModal] = useState(false);
	const [cameraModal, setCameraModal] = useState(false);
	const [linkModal, setLinkModal] = useState(false);

	const [replayModal, setReplayModal] = useState(false);

	const options = {
		settings: {
			overlayColor: "#000000",
		},
	};
  
	const fileHandler = (event) => {
        console.log(event.target.files[0])
    }
    const alt = (event) => {
        return(event.target.files[0].name)
    }
    const preview = (event) => {
        return (
            URL.createObjectURL(event.target.files[0])
        )
    }
  
  
  return (
    <Fragment>
      <PageTitle activeMenu="Profile" motherMenu="App" />

      
      <div className="row">	
        <div className="col-xl-12">
          <div className="card">
            <div className="card-body">
			<div className="pt-3">
								<div className="settings-form">
									<h4 className="text-primary">Votre Informations</h4>
									<form onSubmit={(e) => e.preventDefault()}>
										<div className="form-row">
											<div className="form-group col-md-6">
												<label>Email</label>
												<input type="email" placeholder="Email" className="form-control"/>
											</div>
											<div className="form-group col-md-6">
												<label>Password</label>
												<input type="password" placeholder="Password" className="form-control"/>
											</div>
										</div>
										<div className="form-group">
											<label>Address</label>
											<input type="text" placeholder="390, Bd Zerktouni angle rue Tamaarouft" className="form-control"/>
										</div>
										<div className="form-group">
											<label>Address 2</label>
											<input type="text" placeholder="Bourgogne - Casablanca" className="form-control"/>
										</div>
										<div className="form-row">
											<div className="form-group col-md-6">
												<label>Nom de L'agence</label>
												<input type="text" placeholder="Softway Cars" className="form-control" />
											</div>
											<div className="form-group col-md-6">
												<label>sarl au capital</label>
												<input type="text" placeholder="1.000.000.00 DH" className="form-control" />
											</div>
											<div className="form-group col-md-3">
												<label>RC</label>
												<input type="text" placeholder="166141" className="form-control" />
											</div>
											<div className="form-group col-md-3">
												<label>PAT</label>
												<input type="text" placeholder="35490412" className="form-control" />
											</div>
											<div className="form-group col-md-3">
												<label>IF</label>
												<input type="text" placeholder="1106237" className="form-control" />
											</div>
											<div className="form-group col-md-3">
												<label>CNSS</label>
												<input type="text" placeholder="7522297" className="form-control" />
											</div>
											<div className="form-group col-md-4">
												<label>TEL</label>
												<input type="text" placeholder="0522 267 245" className="form-control" />
											</div>
											<div className="form-group col-md-4">
												<label>FAX</label>
												<input type="text" placeholder="0522 471 640" className="form-control" />
											</div>
											<div className="form-group col-md-4">
												<label>GSM</label>
												<input type="text" placeholder="0661 411 199" className="form-control" />
											</div>
											
										</div>
										<button className="btn btn-primary" type="submit">Enregistrer</button>
									</form>
								</div>
							</div>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default AppProfile;
